CREATE TABLE EMPLOYEE (employee_id integer generated by default as identity, emp_first_name varchar(255), emp_last_name varchar(255), email varchar(255), emp_department varchar(255), emp_project varchar(255), emp_sal numeric(10,2), emp_date_of_joining TIMESTAMP default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP not null, update_timestamp timestamp(6), primary key (employee_id))